mapper.select:


             and name like concat('%', #{name}, '%')
             and name_in = #{nameIn}
             and name_en = #{nameEn}
             and name_ru = #{nameRu}
             and cover_images = #{coverImages}
             and category = #{category}
             and type = #{type}
             and reward_product = #{rewardProduct}
             and daily_interest = #{dailyInterest}
             and cycle = #{cycle}
             and minimum_buy = #{minimumBuy}
             and maximum_buy = #{maximumBuy}
             and fund_type = #{fundType}
             and progress = #{progress}
             and show_progress_bar = #{showProgressBar}
             and selling_timestamp = #{sellingTimestamp}
             and total_fund = #{totalFund}
             and copies = #{copies}
             and is_visible = #{isVisible}
             and on_sale = #{onSale}
             and has_group_buy_option = #{hasGroupBuyOption}
             and lucky_number_range_start = #{luckyNumberRangeStart}
             and lucky_number_range_end = #{luckyNumberRangeEnd}
             and status = #{status}
             and description = #{description}
             and description_en = #{descriptionEn}
             and description_in = #{descriptionIn}
             and description_ru = #{descriptionRu}

				AND date_format(create_time,'%y%m%d') >= date_format(#{params.beginTime},'%y%m%d')


				AND date_format(create_time,'%y%m%d') <= date_format(#{params.endTime},'%y%m%d')


			${params.dataScope}

        order by create_time desc

mapper.update:
update pan_product
name = #{name}, name_in = #{nameIn}, name_en = #{nameEn}, name_ru = #{nameRu}, cover_images = #{coverImages}, category = #{category}, type = #{type}, reward_product = #{rewardProduct}, daily_interest = #{dailyInterest}, cycle = #{cycle}, minimum_buy = #{minimumBuy}, maximum_buy = #{maximumBuy}, fund_type = #{fundType}, progress = #{progress}, show_progress_bar = #{showProgressBar}, selling_timestamp = #{sellingTimestamp}, total_fund = #{totalFund}, curr_fund = #{currFund}, copies = #{copies}, is_visible = #{isVisible}, on_sale = #{onSale}, has_group_buy_option = #{hasGroupBuyOption}, lucky_number_range_start = #{luckyNumberRangeStart}, lucky_number_range_end = #{luckyNumberRangeEnd}, status = #{status}, description = #{description}, description_en = #{descriptionEn}, description_in = #{descriptionIn}, description_ru = #{descriptionRu}, is_draws =#{isDraws}, is_voucher =#{isVoucher}, voucher_start =#{voucherStart}, voucher_end =#{voucherEnd}, update_time = now()
where id = #{id}
