mapper.select:
        SELECT
            recharge_id,
            pan_recharge.user_id,
            sys_user.user_name user_name,
            u2.user_name top_name,
            u3.user_name manager_name,
            u4.user_name agent_name,
            amount,
            pan_recharge.STATUS,
            pan_recharge.create_time,
            order_no,
            pay_info_url,
            request_no,
            order_date,
            pan_recharge.channel_id
        FROM
            pan_recharge
        LEFT JOIN sys_user ON sys_user.user_id = pan_recharge.user_id
        left join sys_user u2 on sys_user.top_id = u2.user_id
        left join sys_user u3 on sys_user.manager_id  = u3.user_id
        LEFT JOIN sys_user u4 ON u4.user_id = sys_user.agent_id

             and sys_user.top_id  = #{topId}
             and sys_user.manager_id  = #{managerId}
             and u2.user_name = #{topName} 
             and u3.user_name = #{managerName} 
             and sys_user.agent_id  = #{agentId}
             and u4.user_name  = #{agentName}
        	 and pan_recharge.user_id  = #{userId}
             and sys_user.user_name  like concat('%',#{userName})
             and pan_recharge.status = #{status}
             and pan_recharge.order_no = #{orderNo}
             and pan_recharge.request_no = #{requestNo}

            	AND  DATE_FORMAT(pan_recharge.create_time,'%Y-%m-%d') >= DATE_FORMAT( #{params.beginTime},'%Y-%m-%d') 


				AND  DATE_FORMAT(pan_recharge.create_time,'%Y-%m-%d') <= DATE_FORMAT( #{params.endTime},'%Y-%m-%d') 


			${params.dataScope}

        order by pan_recharge.create_time desc

SELECT
IFNULL(sum(amount),0)
FROM
pan_recharge
LEFT JOIN sys_user ON sys_user.user_id = pan_recharge.user_id
left join sys_user u2 on sys_user.top_id = u2.user_id
left join sys_user u3 on sys_user.manager_id  = u3.user_id
LEFT JOIN sys_user u4 ON u4.user_id = sys_user.agent_id

     and sys_user.top_id  = #{topId}
     and sys_user.manager_id  = #{managerId}
     and u2.user_name = #{topName} 
     and u3.user_name = #{managerName} 
     and sys_user.agent_id  = #{agentId}
     and u4.user_name  = #{agentName}
     and pan_recharge.user_id  = #{userId}
     and sys_user.user_name like concat('%',#{userName})
     and pan_recharge.status = #{status}
     and pan_recharge.order_no = #{orderNo}
     and pan_recharge.request_no = #{requestNo}

        AND  DATE_FORMAT(pan_recharge.create_time,'%Y-%m-%d') >= DATE_FORMAT( #{params.beginTime},'%Y-%m-%d') 


        AND  DATE_FORMAT(pan_recharge.create_time,'%Y-%m-%d') <= DATE_FORMAT( #{params.endTime},'%Y-%m-%d') 


    ${params.dataScope}

mapper.update:
update pan_recharge
status = #{status}, order_no = #{orderNo}, pay_info_url = #{payInfoUrl}, request_no = #{requestNo}, channel_id = #{channelId}, order_date = now()
where recharge_id = #{rechargeId}
